  margin: auto;
  overflow: hidden;
  width: 600px;
  background-color: #fff;
}
.form-group {
  padding: 0.75rem 0;
  overflow: hidden;
  position: relative;
}

.personal-info input,
textarea {
  display: block;
  margin: auto;
  font-size: 1.4rem;
  line-height: 1.5;
  width: 80%;
  padding: 0.9rem;
  background-color: #fff;
  border: 1px solid #e4e3e3;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  border-radius: 2px;
}
.personal-info label {
  color: #5c1182;
  font-size: 12px;
  transform: translateY(-173%);
  background-color: #fff;
  padding: 2px 4px;
  position: absolute;
  top: 50%;
  transition: transform 0.25s linear;
}
::after {
  box-sizing: border-box;
}
